def sort_list(numbers): 

    # Use bubble sort
    n = len(numbers) 
    for i in range(n): 
        for j in range(0, n-i-1): 

            # If two elements are not in sorted order 
            if numbers[j] > numbers[j+1] : 
                numbers[j], numbers[j+1] = numbers[j+1], numbers[j] 

    return numbers

