import re

def find_frequency(text, phrase):
    text = re.sub('[^a-zA-Z\s]', ' ', text) # removes non alphanumeric characters
    return frequency(text.lower().split(), phrase.lower())

def frequency(wordlist, phrase):
    f_counts = defaultdict(int)
    for word in wordlist:
        if word == phrase:
            f_counts[word] += 1
    return {k: v for (k,v) in sorted(f_counts.items() , key=lambda item: item[1], reverse=True)}

if __name__ == '__main__':
    text = "This is a phrase that I want to find the frequency of"
    phrase = "phrase"
    print(find_frequency(text, phrase))

